changeSet.sql:
update BILLING_DISCOUNT_LANGUAGE set creator = (select username
			from adm_user where BILLING_DISCOUNT_LANGUAGE.creator_id =
			adm_user.id and BILLING_DISCOUNT_LANGUAGE.creator_id is not null)

update BILLING_DISCOUNT_LANGUAGE set updater = (select username
			from adm_user where BILLING_DISCOUNT_LANGUAGE.updater_id =
			adm_user.id and BILLING_DISCOUNT_LANGUAGE.updater_id is not null)

update RM_SERVICE_PARAM_TEMPLATE set creator = (select username
			from adm_user where RM_SERVICE_PARAM_TEMPLATE.creator_id =
			adm_user.id and RM_SERVICE_PARAM_TEMPLATE.creator_id is not null)

update RM_SERVICE_PARAM_TEMPLATE set updater = (select username
			from adm_user where RM_SERVICE_PARAM_TEMPLATE.updater_id =
			adm_user.id and RM_SERVICE_PARAM_TEMPLATE.updater_id is not null)
